
public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// for loops 
		// while loops
		
		String myName = "ProSmart";
		int MySum = 100;
		
		for (int i = 1; i<=3; i++){
			System.out.println(myName+" "+i);
		}
		
		
		for (int j = 1; j<=10; j++){
			MySum = MySum + 2;
		}
		System.out.println(MySum);
		
		int l = 1;
		while (l <= 10){
			System.out.println(myName+" "+l);
			l++;
		}
		
	}

}
